function modalClickFunction(t){switch(t.data.param1){case"reboot":reboot();break;case"shutdown":shutdown();break;default:alert("Action "+t.data.param1+" not recognized.")}}function reboot(){$.ajax({context:this,dataType:"json",url:"/api/Sensor/Reboot",data:null,success:function(){alert("System is rebooting...")}})}function shutdown(){$.ajax({context:this,dataType:"json",url:"/api/Sensor/Shutdown",data:null,success:function(){alert("System is shutting down...")}})}$(function(){$("#okCancelModal").on("show.bs.modal",function(t){var a=$(t.relatedTarget),o=a.data("action"),n=a.data("message"),e=$(this);e.find(".modal-title").text("Confirm action"),e.find(".modal-body p").text(n),e.find("#okCancelModalButton").off("click").click({param1:o},modalClickFunction)})});